% Задача 8: Универсальное множествоU = {'a', 'b', 'c', 'd', 'e', 'f', 'g'};A = [0.3, 0.7, 0.4, 0.2, 0, 0.6, 0];B = [0.3, 0.5, 0.8, 0.8, 0.5, 0.5, 0.6];C = [1, 0.5, 0.2, 0.2, 0, 0.9, 0];% Операцииintersection_AB = min(A, B); % Пересечение A и Bunion_AB = max(A, B); % Объединение A и Bcomplement_A = 1 - A; % Дополнение A% Графикиfigure;bar([A; B; C]', 'grouped');legend('A', 'B', 'C');title('Множества A, B, C');xlabel('Элементы множества');ylabel('Принадлежность');grid on;